        BASE_PATH:  'BASE_PATH',
        FILE_REPO_DIR:  'FILE_REPO_DIR',
        LAZO_PORT:  'LAZO_PORT',
        MODEL_TIMEOUT:  'MODEL_TIMEOUT',
        MODEL_MOCK:  'MODEL_MOCK',
