                                        }
                                    }
                                }

                                if (loaded === components.length) {
                                    options.success(onDone(assets, ctx));
                                }
                            });
                        } else {
                            loaded++;
                            if (loaded === components.length) {
                                options.success(onDone(assets, ctx));
                            }
                        }
                    });


                })(i);
            }
        }

    }, utils);

